const input=document.getElementById("input-box"),output=document.getElementById("output");function codeName(t){let e=t.split(""),n=e.length,u=n%2==0?n:n-1;for(let a=0;a<u;a+=2)[e[a],e[a+1]]=[e[a+1],e[a]];for(let o=0;o<n;o++){let l=e[o].charCodeAt(0);l>=65&&l<=90?l=155-l:l>=97&&l<=122&&(l=219-l),e[o]=String.fromCharCode(l)}return e.join("")}function animateOutput(){output.style.opacity=0,output.style.transform="scale(0.5) translateY(30px)",setTimeout(()=>{output.style.transition="all 0.4s ease-out",output.style.opacity=1,output.style.transform="scale(1.2) translateY(-5px)"},20),setTimeout(()=>{output.style.transition="transform 0.2s ease-in",output.style.transform="scale(1) translateY(0)"},450)}document.getElementById("submit").addEventListener("click",()=>{let t=input.value.trim();if(""===t){output.innerText="⚠️ Please enter your name!",animateOutput();return}let e=/^[a-zA-Z\s]+$/.test(t);if(!e){output.innerText="❌ Only alphabets and spaces are allowed!",animateOutput();return}let n=codeName(t);output.innerText=`${n}`,animateOutput()});